export default function Paginacion(props) {
    //Función que genera hiperbículos a las páginas
    /*const getPaginas = () => {
        const resultado = [];
        for (var i = 0; i < props.total; i++){
            resultado.push(<a className="active" href='#'>{i}</a>);
        }
        return resultado
    }*/

    return (
        <div className="topbar-filter">
            <div className="pagination2">
                <span>Página {props.pagina} de {props.total}:</span>

                {/*getPaginas()*/}
                {Array.apply(0, Array(props.total)).map(value=>
                    <a onClick={props.onChange} className="active" href='#'>{(value+1)}</a>)}



                <a className="active" href="#">1</a>
                <a href="#">2</a>
                <a href="#"><i className="ion-arrow-right-b"></i></a>
            </div>
        </div>
    )

}
